import { Component, OnInit, inject } from "@angular/core";
import { FirebaseService } from "src/app/services/firebase.service";
import { UtilsService } from "src/app/services/utils.service";
import { AddUpdateStudentsComponent } from "src/app/shared/components/add-update-students/add-update-students.component";
import { Html5QrcodeScanner } from "html5-qrcode";

@Component({
  selector: "app-home",
  templateUrl: "./home.page.html",
  styleUrls: ["./home.page.scss"],
})
export class HomePage implements OnInit {
  constructor(
    public firebaseSvc = inject(FirebaseService),
    public utilsSvc = inject(UtilsService),
    public divPhoto = false,
    public html5QrcodeScanner: Html5QrcodeScanner,
    public hrefUrl: string = ""
  ) {}

  ngOnInit(): void {}

  signOut() {
    this.firebaseSvc.signOut();
  }

  addUpdateStudnets() {
    this.utilsSvc.presentModal({
      component: AddUpdateStudentsComponent,
      cssClass: "add-update-modal",
    });
  }

  async takeImage() {
    this.setDivPhoto(true);
    this.delay(1000).then(() => {
      this.html5QrcodeScanner = new Html5QrcodeScanner(
        "reader",
        { fps: 30, qrbox: { width: 250, height: 250 } },
        /* verbose= */ false
      );
      this.html5QrcodeScanner.render(this.onScanSuccess, this.onScanFailure);
    });
  }

  routerlinkIntro() {
    this.utilsSvc.routerLink("/main/intro");
  }

  async onScanSuccess(decodedText, decodedResult) {
    console.log(decodedText.toString());
    this.hrefUrl = decodedText?.toString();
    console.log(this.hrefUrl);
    document.getElementById("html5-qrcode-button-camera-stop").click();
    //console.log(document.getElementById("#hrefUrl"));
    //document.getElementById("#hrefUrl").click();
  }

  onScanFailure(error) {
    console.warn(`Code scan error = ${error}`);
  }

  setDivPhoto = async (b: boolean) => (this.divPhoto = b);

  delay(time) {
    return new Promise((resolve) => setTimeout(resolve, time));
  }
}
