import { Component, OnInit, inject } from "@angular/core";
import { FirebaseService } from "src/app/services/firebase.service";
import { UtilsService } from "src/app/services/utils.service";
import { AddUpdateStudentsComponent } from "src/app/shared/components/add-update-students/add-update-students.component";
import { Html5QrcodeScanner } from "html5-qrcode";

@Component({
  selector: "app-home",
  templateUrl: "./home.page.html",
  styleUrls: ["./home.page.scss"],
})
export class HomePage implements OnInit {
  ngOnInit(): void {}

  firebaseSvc = inject(FirebaseService);
  utilsSvc = inject(UtilsService);
  divPhoto = false;

  signOut() {
    this.firebaseSvc.signOut();
  }

  addUpdateStudnets() {
    this.utilsSvc.presentModal({
      component: AddUpdateStudentsComponent,
      cssClass: "add-update-modal",
    });
  }

  async takeImage() {
    if (!this.divPhoto) {
      this.setDivPhoto(true);
      this.takeImage();
    }
    const html5QrcodeScanner = new Html5QrcodeScanner(
      "reader",
      { fps: 30, qrbox: { width: 250, height: 250 } },
      /* verbose= */ false
    );
    html5QrcodeScanner.render(this.onScanSuccess, this.onScanFailure);
  }

  setDivPhoto = async (b: boolean) => (this.divPhoto = b);

  routerlinkIntro() {
    this.utilsSvc.routerLink("/main/intro");
  }

  onScanSuccess(decodedText, decodedResult) {
    // handle the scanned code as you like, for example:
    console.log(`Code matched = ${decodedText}`, decodedResult);
    this.divPhoto = false;
  }

  onScanFailure(error) {
    // handle scan failure, usually better to ignore and keep scanning.
    // for example:
    console.warn(`Code scan error = ${error}`);
  }
}
